import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useForm } from "react-hook-form";
import { useDispatch, } from "react-redux";


function ProduuctModal () {
  const [show, setShow] = useState(false);
    const dispatch = useDispatch();
const { register, handleSubmit } = useForm();
const [file, setFile] = useState()

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
const addNewProduct = (data)=> {
dispatch(addProduct)({...data, file})
};
  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <form onSubmit={handleSubmit(addNewProduct)}>
          <label htmlFor="Titel">Title</label>
          <input id="title" type="text" {...register("title")} />
          <br />
          <label htmlFor="description">description</label>
          <input id="description" type="text" {...register("desc")} />
          <br />
          <label htmlFor="price">price</label>
          <input id="price" type="number" {...register("price")} />
          <br />
          <label htmlFor="quantity">quantity</label>
          <input id="quantity" type="number" {...register("qty")} />
          <br />
          <label htmlFor="Titel">image</label>
          <input
            id="title"
            type="file"
            accept="image/*"
            onChange={(e) => setFile(e.target.files[0])}
          />
        </form>
        <button>ADD</button>
      </Modal>
    </>
  );
  }

export default ProduuctModal;
