import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import { updateProducts as updateProductAction } from "../actions/productActions";

function ProduuctModalUpdate({ product}) {
  const dispatch = useDispatch();
  const { register, handleSubmit, reset } = useForm({defaultValues:{...product}});
  const [file, setFile] = useState();

 const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const updateProducts = (data) => {
    dispatch(updateProductAction({ ...data, file }));
    reset();
    handleClose();
  };
  return (
    <>
      
      <button variant="primary" onClick={handleShow}>
        update
      </button>

      <Modal show={show} onHide={handleClose}>
        <form onSubmit={handleSubmit(updateProducts)}>
          <label htmlFor="Titel">Title</label>
          <input id="title" type="text" {...register("title")} required />
          <br />
          <label htmlFor="description">description</label>
          <input id="description" type="text" {...register("desc")} required />
          <br />
          <label htmlFor="price">price</label>
          <input id="price" type="number" {...register("price")} required />
          <br />
          <label htmlFor="quantity">quantity</label>
          <input id="quantity" type="number" {...register("qty")} required />
          <br />
          <label htmlFor="Titel">image</label>
          <input
            id="title"
            type="file"
            accept="image/*"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
          <button>ADD</button>
        </form>
      </Modal>
    </>
  );
}

export default ProduuctModalUpdate;
