import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import { addProducts as addNewProduct } from "../actions/productActions";

function ProductModal() {
  const dispatch = useDispatch();
  const { register, handleSubmit, reset } = useForm();
  const [file, setFile] = useState();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const addNewProduct = (data) => {
    dispatch(addProducts({ ...data, file }));
    reset();
    handleClose();
  };

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Add Product
      </Button>

      <Modal show={show} onHide={handleClose}>
        <form onSubmit={handleSubmit(addNewProduct)}>
          <Modal.Header closeButton>
            <Modal.Title>Add Product</Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <label htmlFor="title">Title</label>
            <input id="title" type="text" {...register("title")} required />
            <br />
            <label htmlFor="description">Description</label>
            <input
              id="description"
              type="text"
              {...register("description")}
              required
            />
            <br />
            <br />
            <label htmlFor="price">Price</label>
            <input id="price" type="number" {...register("price")} required />
            <br />
            <label htmlFor="quantity">Quantity</label>
            <input
              id="quantity"
              type="number"
              {...register("quantity")}
              required
            />
            <br />
            <label htmlFor="image">Image</label>
            <input
              id="image"
              type="file"
              accept="image/*"
              onChange={(e) => setFile(e.target.files[0])}
              required
            />
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Add
            </Button>
          </Modal.Footer>
        </form>
      </Modal>
    </>
  );
}

export default ProductModal;
