import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useForm } from "react-hook-form";
import { useDispatch, } from "react-redux";
import {addProducts  } from "../actions/productActions";


function ProductModal() {

  const dispatch = useDispatch();
  const { register, handleSubmit, reset } = useForm();
  const [file, setFile] = useState();
const[show, setShow] = useState()
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const addNewProduct = (data) => {};

  dispatch(addProducts({...data, file}));
reset();
handleClose();
  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        aaaaaaaaaaaaaaaavvvvvvvv
      </Button>

      <Modal show={show} onHide={handleClose}>
        <form onSubmit={handleSubmit(addNewProduct)}>
          <label htmlFor="Titel">Title</label>
          <input id="title" type="text" {...register("title")} required />
          <br />
          <label htmlFor="description">description</label>
          <input id="description" type="text" {...register("desc")} required />
          <br />
          <br />
          <label htmlFor="price">price</label>
          <input id="price" type="number" {...register("price")} required />
          <br />
          <label htmlFor="quantity">quantity</label>
          <input id="quantity" type="number" {...register("qty")} required />
          <br />
          <label htmlFor="Titel">image</label>
          <input
            id="title"
            type="file"
            accept="image/*"
            onChange={(e) => setFile(e.target.files[0])}
            required
          ></input>

          <Button onClick={addProducts}>ADD</Button>
        </form>
      </Modal>
    </>
  );
}
