import {
  REGISTER_FAIL,
  REGISTER_SUCCESS,
  LOGIN_FAIL,
  LOGIN_SUCCESS,
} from "../actions/types";

const initState = {
  userInfo: {},
  token: localStorage.getItem('token') || null,
  errors: null,
  isAuth: Boolean (localStorage.getItem('isAuth')) || false,
};

const userReducer = (state = initState, {type, payload}) => {
    switch (type) {
        case LOGIN_FAIL : 
        case REGISTER_FAIL:
            return { ...state, errors: payload } 
            case REGISTER_SUCCESS:
                 return { 
                ...state,
                userInfo: payload.newUser,
                errors:null,
              }
                case LOGIN_SUCCESS :
                   localStorage.setItem('token', 
payload.token)
                   localStorage.setItem('isAuth', true);
                    return { ...state, userInfo: payload.existUser, token: payload.token, isAuth:true, errors:null,};
        default:
            return state
    }
}
export default userReducer
