import axios from "axios";
import { ADD_PRODUCT_FAILED, GET_PRODUCT_FAILED, GET_PRODUCT_LOADING, GET_PRODUCT_SUCCESS } from "./productTypes";


export const addProducts = () => async (dispatch) => {
  try {
    dispatch({ type: GET_PRODUCT_LOADING });
    const res = await axios.get("/api/product");
    dispatch({ type: GET_PRODUCT_SUCCESS, payload: res.data });
  } catch (error) {
    dispatch({ type: GET_PRODUCT_FAILED, payload: error });
  }
};
export const addProduct = (productInfo) => async (dispatch) => {
try {
  const form = new FormData();
  form.append("file", productInfo.file);
  form.append("upload_preset", "decoration");
  const res = await axios.post(
    "https://api.cloudinary.com/v1_1/fatenfayala/upload",
    form
  );
  productInfo.image = res.data.secure_url;
  await axios.post("/api/product", productInfo);
  dispatch(getProducts());
}catch (error) {
dispatch({ type: ADD_PRODUCT_FAILED, payload: error });
}
}


export const updateProducts = (productInfo) => async (dispatch) => {
  try {
    const form = new FormData();
    form.append("file", productInfo.file);
    form.append("upload_preset", "decoration");
    const res = await axios.post(
      "https://api.cloudinary.com/v1_1/fatenfayala/upload",
      form
    );
    productInfo.image = res.data.secure_url;
    await axios.put(`/api/product/${productInfo._id}`, productInfo);
    dispatch(getProducts());
  } catch (error) {
    dispatch({ type: ADD_PRODUCT_FAILED, payload: error });
  }
};
export const deleteProducts = (prodId) => async (dispatch) => {
  try {

    await axios.delete(`/api/product/${prodId}`);
    dispatch(getProducts());
  } catch (error) {
    dispatch({ type: ADD_PRODUCT_FAILED, payload: error });
  }
};
